class Solution:
    #两个指针的解法：
    #key word: 排序 + 重复元素跳过 节省时间并且防止重复结果出现
    def threeSum(self, nums):
        res = []
        nums.sort()
        for i in xrange(len(nums)-2):
            if i > 0 and nums[i] == nums[i-1]:
                continue
            l, r = i+1, len(nums)-1
            while l < r:
                s = nums[i] + nums[l] + nums[r]
                if s < 0:
                    l +=1 
                elif s > 0:
                    r -= 1
                else:
                    res.append((nums[i], nums[l], nums[r]))
                    while l < r and nums[l] == nums[l+1]:
                        l += 1
                    while l < r and nums[r] == nums[r-1]:
                        r -= 1
                    l += 1; r -= 1
        return res
    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        # nums = sorted(nums)
        d = {}
        for i,v in enumerate(nums):
            if v in d:
                d[v].add(i)
            else:
                d[v] = set([i])
        print(d)
        nums = []
        keys = d.keys()
        for i in keys:
            nums.extend([i for j in range(min(len(d[i]),3))])
        # print(nums)
        nums = sorted(nums)
        print(nums)
        d = {}
        for i,v in enumerate(nums):
            if v in d:
                d[v].add(i)
            else:
                d[v] = set([i])
        print(d)
        ret = []
        ret_set = set()
        l = len(nums)
        for i in range(l):
            for j in range(i+1,l,1):
                if (0-(nums[j]+nums[i])) in d:
                    tmp = set(d[-(nums[j]+nums[i])])
                    tmp.difference_update([i,j])
                    for k in tmp:
                        if k > j:
                            tmp_ret = (nums[i],nums[j],nums[k])
                            if tmp_ret not in ret_set:
                                ret_set.add(tmp_ret)
                                ret.append([nums[i],nums[j],nums[k]])
                                break

        return ret








